INSERT INTO `tbl_faq_groups` (`id`, `title`, `sort_order`, `show_site1`, `show_site2`) VALUES
(1, 'Подключение сервиса', 2, 1, 1),
(2, 'О сервисе Кредди', 1, 1, 1),
(3, 'Повторный займ', 3, 1, 1),
(4, 'Возврат займа', 4, 1, 1),
(5, 'Личный кабинет', 5, 1, 1),
(6, 'Пакеты займов', 6, 1, 1),
(7, 'Решение по займу', 7, 1, 1),
(8, 'Безопасность и гарантии', 8, 1, 1);
INSERT INTO `tbl_faq_questions` (`id`, `title`, `answer`, `group_id`, `sort_order`, `show_site1`, `show_site2`) VALUES
(1, 'Что такое Кредди?', '<p>\n	<span style="font-size:10pt;">«Кредди» - это удобный сервис для получения займов, не выходя из дома, на мобильный телефон или банковскую карту. Оплатив подключение можно неоднократно получать займы без дополнительных платежей.</span>\n</p>\n<p>\n	  Круглосуточная поддержка Клиентов через Контактный центр 8-800-555-75-78 (звонок по России бесплатный) или SMS-сообщения.\n</p>\n<p>\n	  Чтобы проверить информацию о своем балансе, сроке действия Пакета, сроках возврата займа,  запросить повторный  займ и многое другое отправьте соответствующий запрос на номер 7570.\n</p>\n<p>\n	  Подробную информацию о SMS-поддержке и формах запросов можно посмотреть в разделе «<a href="https://kreddy.ru/pages/view/smsinfo" target="_blank">SMS-инфо</a>»\n</p>', 2, 8, 1, 1),
(2, 'Что потребуется для подключения сервиса Кредди?', '<p>\n	 <span style="font-size:10pt;">Для подключения сервиса Вам потребуются оригиналы паспорта гражданина РФ и второго документа из перечня:</span>\n</p>\n<p>\n	 -  заграничный паспорт,\n</p>\n<p>\n	 -  водительское удостоверение,\n</p>\n<p>\n	 -  пенсионное удостоверение,\n</p>\n<p>\n	 -  военный билет,\n</p>\n<p>\n	 -  свидетельство ИНН.\n</p>\n<p>\n	 <span style="font-size:10pt;">Также убедитесь, что на компьютере установлена веб-камера и интернет-браузер Chrome или Firefox последней версии. Это потребуется для прохождения </span><span style="font-size:10pt;"><a href="#collapse7">идентификации</a>.</span>\n</p>', 1, 3, 1, 1),
(3, 'Правила пользования сервисом  Кредди', '<p>\n	 Рекомендуем ознакомиться с <a href="http://kreddy.ru/footerLinks/view/rules" target="_blank">Правилами предоставления микрозайма</a> и <a href="http://kreddy.ru/footerLinks/view/distance" target="_blank" style="font-size:10pt;">Договором (Офертой)</a>\n</p>', 2, 10, 1, 0),
(4, 'Как подключить сервис Кредди?', '<p>\n	   Подготовьте <a href="#collapse2">необходимые документы</a> и проверьте исправность web-камеры на компьютере.\n</p>\n<p>\n	 <strong>1 шаг. Регистрация</strong>\n</p>\n<p>\n	 - Зайдите на главную страницу сайта <a href="http://www.kreddy.ru/">www.kreddy.ru</a>, <strong>выберите Пакет Займов</strong> и <strong>канал получения</strong> (мобильный телефон или банковская карта).\n</p>\n<p>\n	<strong style="font-size:10pt;">- Заполните анкету</strong><span style="font-size:10pt;">. (Рекомендуем проверить указанные данные перед отправкой заявки. Корректность заполнения анкеты может повлиять на решение по выдаче займа.)</span>\n</p>\n<p>\n	<span style="font-size:10pt;"></span><strong style="font-size:10pt;">- Пройдите </strong><span style="font-size:10pt;"><a href="#collapse7">идентификацию</a></span><span style="font-size:10pt;">.</span>\n</p>\n<p>\n	 <strong>2 шаг. Решение по займу</strong>\n</p>\n<p>\n	   Получите одобрение на подключение выбранного Пакета займов на выбранный Вами канал получения (по SMS и в Личном кабинете).\n</p>\n<p>\n	 <strong>3 шаг.</strong> <strong>Оплата подключения</strong>\n</p>\n<p>\n	   Оплатите стоимость подключения пакета любым <a href="https://kreddy.ru/pages/view/payments" target="_blank">удобным способом</a>.\n</p>\n<p>\n	   Получите первый займ в течение нескольких минут после оплаты.\n</p>\n<p>\n	 <span style="font-size:10pt;">Используйте полученные деньги на любые цели.</span>\n</p>', 1, 4, 1, 1),
(5, 'Кто может подключить сервис?', '<p>\n	 <span style="font-size:10pt;">Вы можете подключить сервис «Кредди», если:</span>\n</p>\n<ul><li><span style="font-size:10pt;">Вы являетесь гражданином РФ</span></li>\n	<li><span style="font-size:10pt;"></span><span style="font-size:10pt;">У Вас есть оригиналы паспорта гражданина Российской Федерации и второго документа (гиперссылка на перечень);</span></li>\n	<li>\n	<p>\n		 Ваш возраст от 20 до 60 лет;\n	</p>\n	 </li>\n	<li>\n	<p>\n		 Вы зарегистрированы и постоянно проживаете на территории России;\n	</p>\n	 </li>\n	<li>\n	<p>\n		 Используете постоянный номер мобильного телефона,\n	</p>\n	 </li>\n	<li>\n	<p>\n		 Владеете банковской картой, оформленной на Ваше имя*\n	</p>\n	 </li>\n</ul><p>\n</p>\n<p>\n	 * если Вы планируете получать займы на банковскую карту\n</p>', 1, 5, 1, 1),
(6, 'Какие способы получения денег?', '<p>\n	<span style="font-size:10pt;">При оформлении Пакета займов Вам необходимо выбрать канал получения денег. Вы можете получить займ на счет своего мобильного телефона или на счет своей банковской карты.</span>\n</p>\n<p>\n	Первый займ поступит в течение 10 минут после оплаты стоимости подключения Пакета.\n</p>\n<p>\n	При оформлении повторного займа Вы также можете выбрать канал получения займа – на мобильный телефон или банковскую карту. Форму SMS-запроса на повторный займ с указанием канала перечисления можно посмотреть в разделе <a href="https://kreddy.ru/pages/view/smsinfo" target="_blank">«SMS-поддержка»</a>.\n</p>', 1, 6, 1, 1),
(7, 'Что такое идентификация?', '<p>\n	<span style="font-size:10pt;">Идентификация - это процедура подтверждения Вашей личности. Вам необходимо сфотографироваться через веб-камеру, а так же сфотографировать оригиналы документов (паспорт и второй документ, указанный в анкете) в личном кабинете.</span>\n</p>\n<p>\n	Обращаем Ваше внимание, что для прохождения идентификации необходимо использовать один из браузеров - Chrome или Firefox последних версий. Скачать необходимые версии браузеров Вы можете <a href="https://kreddy.ru/pages/view/browser" target="_blank" style="font-size:10pt;">здесь &gt;&gt;</a>\n</p>', 1, 7, 1, 1),
(8, 'В  течение какого времени нужно оплатить стоимость подключения?', '<p>\n	<span style="font-size:10pt;">Вы можете оплатить стоимость подключения к сервису «Кредди» в течение 14-ти дней с момента получения одобрения.</span>\n</p>', 1, 8, 1, 1),
(9, 'Куда будут перечислены деньги?', '<p>\n	<span style="font-size:10pt;">Если при оформлении запроса на займ Вы указали мобильный телефон как канал получения займа, то займ будет перечислен на счет Вашего мобильного телефона, указанного в Анкете.</span>\n</p>\n<p>\n	Если Вы выбрали канал получения займа - на банковскую карту, то займ будет перечислен на счет Вашей банковской карты.\n</p>', 1, 9, 1, 1),
(10, 'Как быстро будет перечислен займ?', '<p>\n	<span style="font-size:10pt;">Займ будет перечислен на счет Вашего мобильного телефона или счет Вашей банковской карты в течение нескольких минут с момента получения оплаты за подключение. На счете Вашего мобильного телефона деньги отразятся в течение часа после оплаты.</span>\n</p>\n<p>\n	Зачисление денег на банковскую карту может варьироваться в зависимости от банка, в котором открыта Ваша карта.\n</p>\n<p>\n	Если Ваш банк поддерживает технологию быстрого зачисления, то деньги на Вашей карте появятся в течение часа после оплаты.\n</p>\n<p>\n	Если Ваш банк не поддерживает технологию быстрого перевода, то зачисление денег может занять от 1 до 3-х дней.\n</p>', 1, 10, 1, 1),
(11, ' Я уже являюсь Клиентом, как мне воспользоваться займом повторно?', '<p>\n	 <span style="font-size:10pt;">Если Ваш Пакет займов активен и нет ограничений по оформлению следующего займа, то Вы можете оформить повторный займ через </span><a href="https://kreddy.ru/account/login" target="_blank">Личный кабинет</a><span style="font-size:10pt;"> или отправить запрос на 7570. Форму SMS-запроса для оформления повторного займа можно посмотреть в разделе «</span><a href="https://kreddy.ru/pages/view/smsinfo" target="_blank">SMS-поддержка</a><span style="font-size:10pt;">».</span>\n</p>\n<p>\n	 Для получения повторного займа через Личный кабинет, необходимо:\n</p>\n<p>\n	 - убедиться в наличие доступных займов, отсутствии ограничений и задолженностей,\n</p>\n<p>\n	 - выбрать в меню «Оформление займа»,\n</p>\n<p>\n	 - выбрать канал получения (банковская карта или мобильный телефон),\n</p>\n<p>\n	 - нажать на «Оформить займ»,\n</p>\n<p>\n	 - подтвердить оформление займа путем введения проверочного кода, который придет Вам SMS-сообщением на номер мобильного телефона.\n</p>\n<p>\n	 Займ будет перечислен на выбранный канал в течение нескольких минут после подтверждения.\n</p>', 3, 0, 1, 1),
(12, 'Я получил первый займ на банковскую карту, могу повторный займ получить на мобильный телефон?', '<p>\n	<span style="font-size:10pt;">Да, Вы можете изменить канал получения займа при оформлении запроса на повторный займ.</span>\n</p>\n<p>\n	Выберите желаемый канал получения при оформлении повторного займа через Личный кабинет или отправьте соответствующее SMS-сообщение. Форму SMS-запроса для оформления повторного займа можно посмотреть в разделе «<a href="https://kreddy.ru/pages/view/smsinfo" target="_blank">SMS-поддержка</a>».\n</p>', 3, 0, 1, 1),
(13, 'Когда можно воспользоваться повторным займом?', '<p>\n	 <span style="font-size:10pt;">Вы можете проверить состояние подключения Пакета займов в Личном кабинете или через SMS-запрос на 7570.</span>\n</p>\n<p>\n	     В статусе отображается наличие доступных займов и дата, когда Вы можете воспользоваться повторным займом.\n</p>\n<p>\n	     Если повторный займ доступен, оформите запрос на получение займа через Личный кабинет или с помощью SMS- запроса на 7570. <a href="#collapse20">Как оформить запрос на повторный займ?</a>\n</p>', 3, 0, 1, 1),
(14, 'Как можно вернуть займ?', '<p>\n	<span style="font-size:10pt;">Вы можете вернуть займ через сети терминалов QIWI, Элекснет, DeltaPay, через Вашу банковскую карту и со счета Вашего мобильного телефона.</span>\n</p>\n<p>\n	<strong>Оплата через </strong><strong>QIWI: </strong><a href="https://kreddy.ru/pages/view/qiwi"><strong>https://kreddy.ru/pages/view/qiwi</strong></a><strong></strong>\n</p>\n<p>\n	<strong>Оплата через Элекснет: </strong><a href="https://kreddy.ru/pages/view/elecsnet"><strong>https://kreddy.ru/pages/view/elecsnet</strong></a><strong></strong>\n</p>\n<p>\n	<strong>Оплата через DeltaPay: </strong><a href="https://kreddy.ru/pages/view/deltapay"><strong>https://kreddy.ru/pages/view/deltapay</strong></a><strong></strong>\n</p>\n<p>\n	<strong>Оплата через Вашу банковскую карты: </strong><a href="https://kreddy.ru/pages/view/cc"><strong>https://kreddy.ru/pages/view/cc</strong></a><strong></strong>\n</p>\n<p>\n	<strong>Оплата со счета Вашего мобильного телефона (с которого регистрировались при получении займа):  </strong><a href="https://kreddy.ru/pages/view/paymobile"><strong>https://kreddy.ru/pages/view/paymobile</strong></a><strong></strong>\n</p>', 4, 0, 1, 1),
(15, 'Можно ли досрочно погасить займ?', '<p>\n	<span style="font-size:10pt;">Вы можете вернуть займ в любое время до истечения его срока пользования.</span>\n</p>', 4, 0, 1, 1),
(16, 'Доступно частичное погашение займа? ', '<p>\n	<span style="font-size:10pt;">Да, это возможно через терминалы </span><a href="https://kreddy.ru/pages/view/elecsnet" target="_blank">Элекснет</a><span style="font-size:10pt;">, </span><a href="https://kreddy.ru/pages/view/cc" target="_blank">банковской картой</a><span style="font-size:10pt;"> или </span><a href="https://kreddy.ru/pages/view/paymobile" target="_blank">со счета Вашего мобильного телефона</a><span style="font-size:10pt;">.</span>\n</p>', 4, 0, 1, 1),
(17, 'Что я могу сделать в личном кабинете?', '<p>\n	<span style="font-size:10pt;">В личном кабинете Вы можете узнать статус Вашего Пакета, займа, историю Ваших платежей, баланс, сроки возврата займа и действия Пакета, изменить данные, получить повторный займ или подключить новый Пакет займов.</span>\n</p>', 5, 0, 1, 1),
(18, 'Как изменить персональные данные?', '<p>\n	   Вы можете в любой момент изменить свои данные в <a href="https://kreddy.ru/account/login" target="_blank">личном кабинете</a>,<span style="font-size:10pt;"> данный процесс проходит в 2 этапа:</span>\n</p>\n<p>\n	   - 1-й этап - прохождение <a href="#collapse2">идентификации</a> на сайте\n</p>\n<p>\n	   - 2-й этап – заполнение формы подтверждения документов с указанием причины изменения данных или документов.\n</p>', 5, 0, 1, 1),
(19, 'Как подключить новый Пакет займов?', '<p>\n	  Подключение нового Пакета займов возможно в Вашем <a href="https://kreddy.ru/account/login" target="_blank">личном кабинете</a>.\n</p>\n<p>\n	  Если Вы подключаете Пакет займов впервые, <a href="#collapse4">ознакомьтесь</a> с основными вопросами по подключению сервиса\n</p>', 5, 0, 1, 1),
(20, 'Как оформить повторный займ?', '<p>\n	  Оформление повторного займа возможно в Вашем <a href="https://kreddy.ru/account/login" target="_blank">личном кабинете</a>.\n</p>\n<p>\n	Для получения повторного займа через Личный кабинет необходимо:\n</p>\n<p>\n	- убедиться в наличии доступных займов, отсутствии ограничений и задолженностей,\n</p>\n<p>\n	- выбрать в меню «Оформление займа»,\n</p>\n<p>\n	- выбрать канал получения (банковская карта или мобильный телефон),\n</p>\n<p>\n	- нажать на «Оформить займ»,\n</p>\n<p>\n	- подтвердить оформление займа проверочным кодом, который придет Вам SMS-сообщением на номер мобильного телефона, указанный в Анкете.\n</p>\n<p>\n	Займ будет перечислен на выбранный канал в течение нескольких минут после подтверждения.\n</p>', 5, 0, 1, 1),
(21, 'Что такое Пакет?', '<p>\n	<span style="font-size:10pt;">Это возможность дистанционного, неоднократного получения займов в течение определенного времени. Подключение Пакета займов - платное, стоимость Пакета зависит от условий, которые Вы выберете.</span>\n</p>', 6, 1, 1, 1),
(22, 'Какие Пакеты и на каких условиях Вы предоставляете?', '<p>\n	Кредди 1 000 - Вы можете брать по 1 000 руб. 2 раза в течение 14-ти дней, вернуть заем необходимо в течение недели после получения. Стоимость пакета на 14 дней – 200 руб. Данный займ можно получить на карту Кредди (при её наличии) или на счёт мобильного телефона.\n</p>\n<p>\n	Пакет «Покупки» - Вы можете брать по 2 000 руб. 3 раза в течение 25-ти дней, вернуть заем необходимо в течение недели после получения. Стоимость пакета –  600 руб. Данный займ можно получить на карту Кредди (при её наличии) или на счёт мобильного телефона.\n</p>\n<p>\n	Кредди 3 000 - Вы можете брать по 3 000 руб. 2 раза в течение 30-ти дней вернуть заем необходимо в течение недели после получения. Стоимость пакета на 30 дней - 650 руб. Данный займ можно получить на карту Кредди (при её наличии) или на счёт мобильного телефона.\n</p>\n<p>\n	Кредди 5 000 - Вы можете брать по 5 000 руб. 2 раза в течение 30-ти дней вернуть заем необходимо в течение недели после получения. Стоимость пакета на 30 дней – 1000 руб. Данный займ можно получить на карту Кредди (при её наличии) или на счёт мобильного телефона.\n</p>', 6, 2, 1, 1),
(23, 'Сколько времени мне придется ждать решения?', '<p>\n	 Проверка Вашей анкеты, заполненной на сайте kreddy.ru , осуществляется в любой день с 09:00-22:00 по московскому времени и принятие решения занимает обычно 20 минут.\n</p>', 7, 3, 1, 1),
(24, 'Мне пришло одобрение на другой Пакет займов', '<p>\n	Система автоматически определяет размер Пакета займов, который Вам может быть доступен на текущий момент. При регулярном пользовании нашим сервисом, Вам станут доступны все пакеты займов.\n</p>', 7, 4, 1, 1),
(25, 'Мне отказали в подключении пакета займов. Я могу отправить запрос еще раз?', '<p>\n	В случае отсутствия одобрения на подключение Пакета займов, Вы можете повторно отправить заявку через 2 месяца. Точную дату отправки запроса можно уточнить в <a href="https://kreddy.ru/account/login" target="_blank">личном кабинете</a> или отправив SMS-сообщение со словом: Pak на 7570.\n</p>', 7, 5, 1, 1),
(26, 'Какие гарантии Вы можете предоставить, что займ будет мне перечислен после оплаты подключения пакета услуг?', '<p>\n	 Компания ООО «Финансовые Решения» (оператор сервиса «Кредди») является официальной микрофинансовой организацией, которая внесена в государственный реестр микрофинансовых организаций. <a href="https://kreddy.ru/footerLinks/view/svid" target="_blank" style="font-size:10pt;">Свидетельство о внесении в государственный реестр</a>\n</p>\n<p>\n	Все действия по предоставлению услуги регулируются законодательством РФ. Из договора микрозайма и оферты вытекает обязанность нашей Компании перечислить Вам денежные средства.  Это регламентируется статьей 307 ГК РФ. При оформлении заявки на подключение Пакета займа и оплаты самого подключения Вы принимаете условия договора с нашей Компанией,  что обязывает нас надлежащим способом оказывать услуги и исполнять все обязательства по договору и в соответствии с требованиями закона (ст. 309 ГК РФ). В соответствии со статьей 310 ГК РФ односторонний отказ от исполнения обязательства и одностороннее изменение его условий не допускаются.\n</p>\n<p>\n	Чтобы проверить как работает наш сервис и оценить сроки зачисления средств, Вы можете протестировать канал получения займа за 1 руб. <a href="https://kreddy.ru/pages/view/check" target="_blank" style="font-size:10pt;">Подробности &gt;&gt;&gt;</a>\n</p>\n<p>\n	Также 24 часа в сутки и 7 дней в неделю Вы можете обратиться в Контактный центр по телефону 8-800-555-75-78 (звонок по России бесплатный) для уточнения статуса по переводу займа или в любое время воспользоваться службой <a href="https://kreddy.ru/pages/view/smsinfo" target="_blank">SMS-поддержки</a>.\n</p>', 8, 6, 1, 1),
(27, 'Как Вы защитите данные моей банковской карты? ', '<p>\n	Данные всех банковских карт наших Клиентов надежно защищены в соответствии с международными стандартами PCI DSS. Вся информация хранится в зашифрованном виде и с соблюдением жестких стандартов по безопасности. Ежегодно наш сервис проходит строгий аудит и сертификацию на соответствие международным требованиям и нормам защиты данных банковских карт.  Вы можете посмотреть <a href="https://kreddy.ru/static/docs/pcidss.pdf" target="_blank">сертификат</a>, подтверждающий соответствие стандартам PCI DSS<em>.</em>\n</p>', 8, 7, 1, 1),
(28, 'Правила пользования сервисом  Кредди', '<p>\n	 Рекомендуем ознакомиться с <a href="http://kreddy.ru/footerLinks/view/rules_ivanovo" target="_blank">Правилами предоставления микрозайма</a> и <a href="http://kreddy.ru/footerLinks/view/offer_ivanovo" target="_blank" style="font-size:10pt;">Договором (Офертой)</a>\n</p>', 2, 9, 0, 1);
